const showFirst=document.querySelectorAll(".showFirst"),showSecond=document.querySelectorAll(".showSecond"),timer=a=>new Promise(b=>setTimeout(b,a));async function first(){for(let a=0;a<showFirst.length;a++)showFirst[a].style.display="block",await timer(80)}first();async function second(){for(let a=0;a<showSecond.length;a++)showSecond[a].style.display="block",await timer(60)}setTimeout(()=>{second()},1800);const slides=document.querySelectorAll(".shop__categories");for(const a of slides){function b(){slides.forEach(a=>{a.classList.remove("shop__categories--active")})}a.addEventListener("click",()=>{b(),a.classList.add("shop__categories--active")})}